import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router';
import { FaTrashAlt, FaStar } from 'react-icons/fa';

const BookList = ({ onDelete }) => {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:2000/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching books:", error);
    }
  };
  const handleDelete = async (bookId) => {
    console.log("Deleting book with ID:", bookId);
    
    try {
      // Make a DELETE request to the server to delete the book by ID
      const response = await axios.delete(`http://localhost:2000/api/books/${bookId}`);
      
      // After successful deletion, update the state by removing the deleted book
      setBooks(books.filter(book => book._id !== bookId));
  
      console.log("Book deleted successfully:", response.data);
      al
    } catch (error) {
      console.error("Error deleting book:", error);
    }
  };

  const handleClick = () => {
    navigate('/add');
  };

  return (
    <div style={{ backgroundColor: "" }}>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h2 className="text-2xl font-bold mb-4">Book List</h2>
        <button
          className="text-2xl font-bold mb-4"
          onClick={handleClick}
        >
          Add Book
        </button>
      </div>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '16px' }}>
        {books.map(book => (
          <div
            key={book._id}
            style={{
              width: 'calc(25% - 16px)', // 4 items per row with a gap
              boxSizing: 'border-box',
              marginBottom: '16px',
            }}
          >
            <div style={{ padding: '10px', border: '1px solid #ddd', borderRadius: '8px' }}>
              <img
                src={`http://localhost:2000${book.image}`}
                alt="Book"
                style={{ width: '100%', height: '250px', borderRadius: '8px' }}
              />
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0' }}>
                <div>{book.title}</div>
                <div>{book.author}</div>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', padding: '8px 0' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                  {Array.from({ length: book.rating }, (_, index) => (
                    <FaStar key={index} style={{ color: 'gold', marginRight: '2px' }} />
                  ))}
                </div>

                <button
                  onClick={() => handleDelete(book._id)} 
                  style={{ marginLeft: '10px', cursor: 'pointer', border: 'none', background: 'transparent' }}
                >
                  <FaTrashAlt style={{ color: 'red' }} />
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BookList;
