import React, { useState } from 'react';
import axios from 'axios';
import './boo.css'; // Import the CSS file

const AddBookForm = ({ onAdd }) => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [description, setDescription] = useState('');
  const [rating, setRating] = useState('');
  const [comments, setComments] = useState('');
  const [image, setImage] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title || !author || !description || !rating || !comments || !image) {
      alert("All fields are required");
      return;
    }

    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('description', description);
    formData.append('rating', rating);
    formData.append('comments', comments);
    formData.append('image', image);

    try {
      await axios.post('http://localhost:2000/api/books', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      onAdd();
      setTitle('');
      setAuthor('');
      setDescription('');
      setRating('');
      setComments('');
      setImage(null);
    } catch (error) {
      console.error("Error adding book:", error);
    }
  };

  return (
    <div className="form-container">
      <h2>Add a New Book</h2>
      <form onSubmit={handleSubmit} className="mb-4">
        <div className="form-group">
          <label className="input-label">Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Title"
            className="input-field"
          />
        </div>
        <div className="form-group">
          <label className="input-label">Author</label>
          <input
            type="text"
            value={author}
            onChange={(e) => setAuthor(e.target.value)}
            placeholder="Author"
            className="input-field"
          />
        </div>
        <div className="form-group">
          <label className="input-label">Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Description"
            className="textarea-field"
          />
        </div>
        <div className="form-group">
          <label className="input-label">Rating (1-5)</label>
          <input
            type="number"
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            placeholder="Rating (1-5)"
            min="1"
            max="5"
            className="input-field"
          />
        </div>
        <div className="form-group">
          <label className="input-label">Comments</label>
          <textarea
            value={comments}
            onChange={(e) => setComments(e.target.value)}
            placeholder="Comments"
            className="textarea-field"
          />
        </div>
        <div className="form-group">
          <label className="input-label">Book Image</label>
          <input
            type="file"
            onChange={(e) => setImage(e.target.files[0])}
            className="file-input"
          />
        </div>
        <button type="submit" className="bg-blue-500 text-white p-2 rounded">
          Add Book
        </button>
      </form>
    </div>
  );
};

export default AddBookForm;
