// src/components/BookList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BookList = ({ onDelete }) => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:2000/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching books:", error);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h2 className="text-2xl font-bold mb-4">Book List</h2>
        <button className="text-2xl font-bold mb-4">Add Book</button>

      </div>
      <ul>
        {books.map(book => (
          <li key={book._id} className="bg-white border rounded-lg shadow-md p-4 mb-4 hover:shadow-lg transition-shadow duration-300">
          <div className="flex items-center mb-4">
            <img 
              src={`http://localhost:2000${book.image}`} 
              alt="Book" 
              className="w-24 h-24 object-cover rounded-md mr-4" 
            />
            <div>
              <h3 className="font-semibold text-xl text-gray-800">{book.title}</h3>
              <p className="text-gray-600">{book.author}</p>
            </div>
          </div>
          <p className="text-gray-700 mb-4">{book.description}</p>
          <button
            onClick={() => onDelete(book._id)}
            className="text-red-500 hover:text-red-700 mt-2 py-2 px-4 border border-red-500 rounded-lg text-sm"
          >
            Delete
          </button>
        </li>
        
        ))}
      </ul>
    </div>
  );
};

export default BookList;
