import React from 'react';
import icon from '../assests/book.jpeg'
import './header.css'

const Header = () => {
    return (
        <header className="bg-blue-600 p-4 shadow-md fixed top-0 left-0 w-full z-50">
            <div
                style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    backgroundColor: 'blue',
                    color: 'white',
                    height: '100px'
                }} className="container mx-auto flex items-center justify-between">

                <div className="flex items-center space-x-2 text-white">
                    <img src={icon} alt="Book" className="w-12 h-12"
                        style={{ height: '100px' }} />
                </div>

                <div className="flex items-center space-x-2 text-white">
                    <h1 className="text-xl font-bold">Book Store</h1>
                </div>

                <div style={{ justifyContent: 'space-between',marginRight: 10 }} >
                    <nav className="flex space-x-4 text-white">
                        <a href="/" style={{ color: 'white', marginRight: 10 }} className="hover:underline">Home</a>
                        {/* <a href="/contact" className="hover:underline">Contact   </a> */}
                    </nav>
                </div>
            </div>
        </header>
    );
};

export default Header;
