import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash } from 'react-icons/fa';

const BookList = ({ onDelete }) => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:2000/api/books');
      setBooks(response.data);
    } catch (error) {
      console.error("Error fetching books:", error);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        <h2 className="text-2xl font-bold mb-4">Book List</h2>
        <button className="text-2xl font-bold mb-4">Add Book</button>

      </div>
      <ul>
        {books.map(book => (
          <div style={{ width: '200px', backgroundColor: "blue"}}>
           
              <img
                src={`http://localhost:2000${book.image}`}
                alt="Book"
                style={{ width: '200px' }}
                className="w-24 h-24 object-cover rounded-md mr-4"
              />
            
            <div style={{ display: 'flex', justifyContent: 'space-between', padding: 1 }}>
              <p className="font-semibold text-xl text-gray-800">{book.title}</p>
              <p className="font-semibold text-xl text-gray-800">{book.author}</p>
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
};

export default BookList;
